import './App.css';
import React, { Component } from 'react';

const list = [
  {
    "id": 1,
    "title": "Learn Apis properly",
    "body": "APIs are a way for different software applications to talk to each other. They are used in many industries, like web development, mobile development, and IoT. Learning APIs can be a valuable skill for developers of all levels."
  },
  {
    "id": 2,
    "title": "Conclude my react ecommerce project",
    "body": "APIs are a way for different software applications to talk to each other. They are used in many industries, like web development, mobile development, and IoT. Learning APIs can be a valuable skill for React developers of all levels."
  },
  {
    "id": 3,
    "title": "Finish the paid gig",
    "body": "Learn how to use APIs with React. There are many different ways to use APIs with React. Some common ways include using the fetch() API, using a third-party library like axios, or using a custom hook."
  }
]

class App extends Component {
  constructor(props) {
    super(props);
    this.state = { list };
  }
}

export default App;
